import React from 'react'
import {useState} from 'react'
import Axios from 'axios'
import './ClassList.css'


export const ClassList = () => {

  // const [year,setYear]=useState("")
  // const [cls,setCls]=useState("")
  const [classList, setClassList] = useState([])
  // const clsObj={
  //   cls:cls,
  //   year:year
  // }
  var done=false;
  const callShowList=()=>{
    if(!done){
      showList()
      done=true
    }
    else{
      console.log("Successfully stopped showing list");
    }
  }
  const showList=()=>{
      Axios.get('http://localhost:5000/showClasses').then((response)=>{
          console.log("Retreived!",response.data)
          setClassList(response.data) 
        })
  }
  return (
    <>
      {/* <button type='button' className='btn btn-sm btn-primary' onClick={showList}>Click</button> */}
      {callShowList()}
      {
        classList.map((val,key)=>{
          return(
            <>
            <div className="cls-details">
              <div>{val.classes}</div>
              <div>{val.year}</div>
            </div>
            </>
          )

        })
      }

    </>
  )
}
