import './Login.css'
import Axios  from 'axios'
import { useState } from 'react'
import { EditProf } from './EditProf'

export const Login = () => {
  const [id, setId] = useState("")
  const [pwd,setPwd]=useState("")
  const stud={
    id:id,
    paswd:pwd
  }
  const validate=()=>{
    Axios.post('http://localhost:5000/validatePswd',stud).then((response)=>{
