          }
         })
  }
  return (
    <>
    <div className='cont'>
    <div className="login-container">
        <div id="sin-img"> 
            <img src={symbol} alt=""/>
        </div>
        <div id="sin">Sign in</div>
        <input type="text" placeholder="ID Number" className="ip" onChange={(event)=>{setId(event.target.value)}}/>
